INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1726, 'cath_mv_stenosis_stage', 'stage_d', 'Stage D: Symptomatic severe MS', '<p><span style="background-color: #ffff00;">Mitral Stenosis Stage D:</span><span style="background-color: #ffff00;"> Symptoma</span><span style="background-color: #ffff00;">tic</span><span style="background-color: #ffff00;"> Severe MS</span></p><ul><li>&nbsp;</li></ul>', '<p><strong><span style="background-color: #00ff00;">Valve Anatomy</span></strong></p><ul><li>Rheumatic valve changes with&nbsp;&nbsp; commissural fusion and&nbsp;&nbsp; diastolic doming of the mitral&nbsp;&nbsp; valve leaflets</li><li>Planimetered MVA &lt;=1.5 cm2</li></ul><p><strong><span style="background-color: #00ff00;">Valve Hemodynamics&nbsp;</span></strong></p><ul><li>MVA &lt;=&nbsp;1.5 cm2</li><li>(MVA &lt; =&nbsp;1.0 cm2 with very&nbsp;severe MS)</li><li>Diastolic pressure half-time &gt;&nbsp;150 ms</li><li>(Diastolic pressure half-time &gt;&nbsp;220 ms with very severe MS)<br />&nbsp;</li></ul><p><strong><span style="background-color: #00ff00;">Hemodynamic Consequences</span></strong></p><ul><li>&nbsp;Severe LA enlargement</li><li>Elevated PASP &gt;30 mm Hg</li></ul><p><strong><span style="background-color: #00ff00;">Symptoms</span></strong></p><ul><li>&nbsp;Decreased exercise&nbsp;&nbsp;tolerance</li><li>Exertional dyspnea</li></ul>', 2331, '2016-08-25 11:50:55', '2022-06-10 02:11:34', NULL);
