INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8430, 'cath_ind_chest_pain_type', '1', 'Typical Angina (definite)', '<p>Symptoms meet all three of the characteristics of angina (also known as definite): <br />(View Notes)<br /><br /></p>', 'Symptoms meet all three of the characteristics of angina (also known as definite):<br /><br />1. Substernal chest discomfort with a characteristic quality and duration<br />that is<br /><br />2. provoked by exertion or emotional stress and<br /><br />3. relieved by rest or nitroglycerin.', 3735, '2018-06-14 15:11:52', '2022-06-11 08:00:22', NULL);
