INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (3088, 'elv_lved_volume_method', '4', 'Endocardial border enhancement (contrast echo)', NULL, 'Current indications for contrast agents in Europe state that their use is targeted to &ldquo;patients with suspected or established cardiovascular disease to provide opacification of cardiac chambers and enhance LV endocardial border delineation&rdquo; . <br /><br />In other words, contrast echocardiography represents a tool to improve image quality. Many patients have suboptimal images despite the advances of ultrasound technology. <br /><br />The use of contrast echocardiography is desirable when the anticipated image improvement may alter patient management. <br /><br />Contrast agents also improve the signal-to-noise ratio in colour and spectral Doppler echocardiography. This can be used to rescue noisy Doppler studies, in particular studies of aortic valves and pulmonary venous flow.<br /><br /><div id="CitationTab" class="detail"><p class="detailTitle">Citation:<a class="d-inline-block detailText" href="https://www.ecrjournal.com/editions/european-cardiovascular-disease-2007-volume-3-issue-2">European Cardiovascular Disease 2007;3(2):44&ndash;8</a></p></div><div id="CitationPopup" class="citationPopup"><div class="citationLink">DOI:<a class="d-inline-block detailText" href="https://doi.org/10.15420/ecr.2007.0.2.44">https://doi.org/10.15420/ecr.2007.0.2.44</a></div></div>', 471, '2016-10-31 09:00:31', '2022-06-12 05:00:45', NULL);
