INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9127, 'chest_pain_symptoms', 'Dyspnea', 'Dyspnea', NULL, NULL, 4090, '2022-11-03 01:35:10', '2022-11-03 01:35:10', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9128, 'chest_pain_symptoms', 'Palpitations	', 'Palpitations', NULL, NULL, 4090, '2022-11-03 01:38:09', '2022-11-03 01:38:09', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9129, 'chest_pain_symptoms', 'Diaphoresis', 'Diaphoresis', NULL, 'Diaphoresis refers to&nbsp;<strong>excessive sweating, commonly associated with an underlying medical condition that alters hormone levels in the body</strong>.<br />Those with hyperthyroidism, diabetes mellitus, endocrine tumors, and those who are going through menopause or pregnancy can experience diaphoresis due to changes in hormones.', 4090, '2022-11-03 01:39:04', '2022-11-03 01:42:02', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9130, 'chest_pain_symptoms', 'Nausea_vomiting', 'Nausea or vomiting', NULL, NULL, 4090, '2022-11-03 02:00:37', '2022-11-03 02:00:37', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9131, 'chest_pain_symptoms', 'Lightheadedness', 'Lightheadedness', NULL, 'Lightheadedness is when you feel like you might faint.<br />Your body could feel heavy, you might feel nauseous and unsteady, and you may sweat. <br />Your vision might also be affected.<br /><br />Lightheadedness is often caused by a lack of blood in the brain.<br />Lightheadedness is different from&nbsp;<a href="https://www.healthdirect.gov.au/dizziness">dizziness</a>. <br />Dizziness is when you feel unbalanced and as if the room is spinning. <br />Lightheadedness is when you feel like you might faint. <br />Your body could feel heavy, you might feel&nbsp;<a href="https://www.healthdirect.gov.au/nausea">nauseous</a>&nbsp;and unsteady, and you may sweat. <br />Your vision might also be affected.<br /><br /><br />', 4090, '2022-11-03 02:03:54', '2022-11-03 02:03:54', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9132, 'chest_pain_symptoms', 'Confusion', 'Confusion', 'Confusion is the inability to think as clearly or quickly as you normally do.', 'Confusion is the inability to think as clearly or quickly as you normally do. <br />You may feel disoriented and have difficulty paying attention, remembering, and making decisions.', 4090, '2022-11-03 02:06:07', '2022-11-03 02:06:07', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9133, 'chest_pain_symptoms', 'syncope', 'Presyncope or syncope', NULL, 'Presyncope or near-syncope is often ill-defined and may have different meanings to different providers but denotes near fainting or a prodrome of syncope.<br />The most uniform definition is "feeling like one was going to pass out but without actual loss of consciousness."<br />Near syncope can last for seconds to minutes.<br />Symptoms may be accompanied by a feeling of lightheadedness, general weakness, warmth, diaphoresis, nausea, palpitations or blurry vision<br />&nbsp;Although often perceived as more benign than syncope, data suggest that both the pathophysiology (cerebral hypoperfusion) and outcomes of near syncope mimic those of syncope.<br />This activity outlines the etiologies, epidemiology, evaluation, and management of presyncope and highlights the role of the interprofessional team in caring for affected patients.', 4090, '2022-11-03 02:09:56', '2022-11-03 02:09:56', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9134, 'chest_pain_symptoms', 'Abdomina_symptoms', 'Abdominal symptoms', NULL, NULL, 4090, '2022-11-03 02:14:45', '2022-11-03 02:14:45', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9135, 'chest_pain_symptoms', 'Heartburn', 'Heartburn unrelated to meals', '<p>Heartburn is a burning pain in your chest, just behind your breastbone.&nbsp;</p>', '<p>Heartburn is a burning pain in your chest, just behind your breastbone.&nbsp;</p>', 4090, '2022-11-03 02:17:46', '2022-11-03 02:17:46', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9136, 'chest_pain_symptoms', 'Other', 'Other', NULL, NULL, 4090, '2022-11-03 02:18:16', '2022-11-03 02:18:16', NULL);
