INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9137, 'anginal_equivalent', 'Nausea', 'Nausea', NULL, NULL, 4092, '2022-11-03 02:34:57', '2022-11-03 06:32:28', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9139, 'anginal_equivalent', 'Fatigue', 'Extreme fatigue', NULL, NULL, 4092, '2022-11-03 06:33:19', '2022-11-03 06:33:19', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9140, 'anginal_equivalent', 'Pain_pressure_tightness,', 'Pain, pressure, tightness, or discomfort in shoulders, arms, neck, back, upper abdomen, or jaw', NULL, NULL, 4092, '2022-11-03 06:34:24', '2022-11-03 06:34:24', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9141, 'angina_class', '0', 'Asymptomatic', NULL, NULL, 4093, '2022-11-03 06:40:00', '2022-11-03 06:40:00', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9142, 'angina_class', '1', 'Class I', 'Ordinary physical activity, such as walking or climbing stairs, does not cause angina. <br />Angina occurs with strenuous, rapid, or prolonged exertion at work or recreation.', NULL, 4093, '2022-11-03 06:41:25', '2022-11-03 06:41:25', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9143, 'angina_class', '2', 'Class II', 'Slight limitation of ordinary activity.<br />Angina occurs on walking or climbing stairs rapidly, walking uphill, walking or climbing stairs after meals, or in cold, in wind, or under emotional stress, or only during the few hours after awakening. <br />Angina occurs on walking &gt;2 blocks on the level and climbing &gt;1 flight of ordinary stairs at a normal pace and in normal conditions.', 'The blocks in European Countries are Smaller than the American Ones', 4093, '2022-11-03 06:44:21', '2022-11-03 06:44:21', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9144, 'angina_class', '3', 'Class III', 'Marked limitation of ordinary physical activity. <br />Angina occurs on walking 1&ndash;2 blocks on the level and climbing 1 flight of stairs in normal conditions and at a normal pace.', NULL, 4093, '2022-11-03 06:45:43', '2022-11-03 06:45:43', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9145, 'angina_class', '4', 'Class IV', 'Inability to perform any physical activity without discomfort; angina symptoms may be present at rest.', NULL, 4093, '2022-11-03 06:46:52', '2022-11-03 06:46:52', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9146, 'angina_class', 'Unknown', 'Unknown', 'A proper value is applicable but not known.', NULL, 4093, '2022-11-03 06:48:09', '2022-11-03 06:48:09', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9162, 'chest_pain_risk_factors', 'Hypertension', 'Hypertension', 'Elevated blood pressure (120&ndash;129 mm Hg systolic/ &lt;80 mm Hg diastolic);<br /><strong>stage</strong> 1 hypertension (130&ndash;139 mm Hg systolic or 80&ndash;89 mm Hg diastolic),<br /><strong>or stage 2</strong> hypertension (&ge;140 mm Hg systolic or &ge;90 mm Hg diastolic)', NULL, 4096, '2022-11-06 03:34:14', '2022-11-06 06:50:22', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9147, 'chest_pain_etiology', 'STEMI', 'STEMI', 'STEMIs are characterized by the presence of both criteria: <br /><br />1) ECG evidence of STEMI: new or presumed new ST-segment elevation at the J-point in 2 contiguous leads with the cut-off point: &ge;1 mm in all leads other than leads V2&ndash;V3 where the following cutpoints apply: &ge;2 mm in men &ge;40 y; &ge;2.5 mm in men &lt;40 y; or &ge;1.5 mm in women regardless of age. (When the magnitudes of J-point elevation in leads V2 and V3 are registered from a prior electrocardiogram, new J-point elevation &ge;1 mm [as compared with the earlier electrocardiogram] should be considered an ischemic response.)<br /><br />2) Detection of a rise or fall of cardiac biomarker values (preferably cTn) with &ge;1 value above the 99th URL', NULL, 4095, '2022-11-03 14:58:57', '2022-11-03 14:58:57', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9148, 'chest_pain_etiology', 'mi_type_2', 'MI, type 2', 'Ischemic myocardial injury in the context of a mismatch between oxygen supply and demand has been classified as type 2 MI.<br /><br />Criteria for type 2 MI:<br />Detection of a rise or fall of cTn concentrations with at least 1 value above the 99th percentile URL, and evidence of an imbalance between myocardial oxygen supply and demand unrelated to acute coronary atherothrombosis, requiring at least 1 of the following:<br /><br />1. Symptoms of acute myocardial ischemia<br />2. Development of pathological Q waves<br />3. Imaging evidence of new loss of viable myocardium or new regional wall motion abnormality in a pattern consistent with an ischemic etiology<br />', NULL, 4095, '2022-11-03 15:07:08', '2022-11-03 15:07:47', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9149, 'chest_pain_etiology', 'nste_acs', 'NSTE-ACS', 'NSTE-ACS encompasses NSTEMI and unstable angina. NSTEMIs are characterized by the presence of both criteria:<br />1) Detection of a rise or fall of cardiac biomarker values (preferably cTn) with &ge;1 value above the 99th URL. ECG changes or ischemic symptoms may or may not be present.<br />2) Absence of ECG changes that are diagnostic of a STEMI (see STEMI).<br /><br />Unstable angina is a condition in which there is angina pectoris that occurs<br /><br />stress or activity, or with decreasing stress or activity compared with stable angina and has been present for &lt;2 wk. It is characterized by the absence of ECG changes that are diagnostic of a STEMI<br /><br />', NULL, 4095, '2022-11-03 15:14:58', '2022-11-03 15:14:58', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9150, 'chest_pain_etiology', 'aortic_syndrom', 'Acute aortic syndrome', 'Acute aortic syndrome includes aortic dissection, intramural hematoma, and symptomatic aortic ulcer.', NULL, 4095, '2022-11-05 14:03:45', '2022-11-05 14:03:45', ' Circulation. 2005;112:3802-3813.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9151, 'chest_pain_etiology', 'pa_embolism', 'Pulmonary Embolism', 'Intravascular migration of a venous thrombus to the pulmonary arterial circulation. It is diagnosed by a positive pulmonary angiogram, an unequivocally positive helical CT scan, a high-probability ventilation-perfusion scan, or autopsy.', NULL, 4095, '2022-11-05 14:06:20', '2022-11-05 14:06:20', 'Circ Cardiovasc Qual Outcomes. 2020;13:e000059.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9152, 'chest_pain_etiology', 'Myocarditis', 'Myocarditis', 'Myocarditis is an inflammatory disease of the myocardium resulting from viral infections or postviral immune-mediated responses<br /><br />Clinical manifestations of myocarditis are varied and include chest pain that is often sharp and reflective of epicardial inflammation involving the pericardium. <br />Myocardial dysfunction often causes fatigue and exercise intolerance, and predominance of heart failure distinguishes myocarditis from pericarditis; cTn is usually elevated.', NULL, 4095, '2022-11-05 14:09:32', '2022-11-05 14:09:32', 'NCDR Chest Pain-MI Auxiliary Coderâs Data Dictionary (element #14617)');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9153, 'chest_pain_etiology', 'Pericarditis', 'Pericarditis', 'Pericarditis classically presents with chest pain that is sharp, pleuritic, and which may be improved by sitting up or leaning forward, although in many instances such findings are not present.<br />A pericardial friction rub may be audible. <br />Widespread ST elevation with PR depression is the ECG hallmark, although changes are nonspecific and may be transient.', NULL, 4095, '2022-11-05 14:11:17', '2022-11-05 14:12:06', 'J Am Coll Cardiol. 2020;75:76-92.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9154, 'chest_pain_etiology', 'Myopericarditis', 'Myopericarditis', 'Pericarditis and myocarditis share overlapping common causes and likely form a continuum. <br />The diagnosis of predominant pericarditis with myocardial involvement, or &ldquo;myopericarditis,&rdquo; can be clinically established if patients with definite criteria for acute pericarditis show elevated biomarkers of myocardial injury (cTn I or T) without newly developed focal or diffuse impairment of LV function in echocardiogram or CMR.', NULL, 4095, '2022-11-05 14:15:12', '2022-11-05 14:15:12', 'Circulation. 2021;144:e368âe454.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9155, 'chest_pain_etiology', 'Valvular', 'Valvular heart disease', 'Valvular heart disease includes the following:<br /><br />Aortic stenosis<br />Aortic regurgitation<br /><br />Mitral stenosis<br />Mitral regurgitation<br />Mixed valve disease<br /><br />Tricuspid valve disease<br />Pulmonic valve disease', NULL, 4095, '2022-11-05 14:35:59', '2022-11-05 14:35:59', 'Circulation. 2021;143:e72-e227.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9156, 'chest_pain_etiology', 'Heart_failure', 'Heart failure', 'Heart failure is a complex clinical syndrome that results from any structural or functional impairment of ventricular filling or ejection of blood. <br />The cardinal manifestations of heart failure are dyspnea and fatigue, which may limit exercise tolerance, and fluid retention, which may lead to pulmonary or splanchnic congestion and/or peripheral edema.<br />There is no single diagnostic test for heart failure because it is largely a clinical diagnosis based on a careful history and physical examination.', '<p>There is no single diagnostic test for heart failure. <br />It is a clinical diagnosis based on a careful history, physical examination, ECG findings, blood chemistry values including natriuretic peptides, and myocardial imaging (with echocardiography being the most commonly used method).<br />A low EF alone, without clinical evidence of heart failure, does not qualify as heart failure.</p>', 4095, '2022-11-05 14:40:49', '2022-11-05 14:40:49', 'Circulation. 2022;145:e895âe1032.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9157, 'chest_pain_etiology', 'Other', 'Other', 'Examples are esophageal rupture, tension pneumothorax, sickle cell chest crisis.', NULL, 4095, '2022-11-05 14:42:18', '2022-11-05 14:42:18', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9159, 'chest_pain_risk_factors', 'male', 'Male sex', NULL, NULL, 4096, '2022-11-06 03:32:54', '2022-11-06 03:32:54', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9170, 'chest_pain_risk_factors', 'Other_genetic_factors', 'Other familial/genetic factors', NULL, NULL, 4096, '2022-11-06 03:39:36', '2022-11-06 03:39:36', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9158, 'chest_pain_risk_factors', 'Age', 'Age &ge;65 y', NULL, NULL, 4096, '2022-11-06 03:32:15', '2022-11-06 06:34:01', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9167, 'chest_pain_risk_factors', 'Alcohol', 'Alcohol intake', 'Women: &gt;1 drink daily', NULL, 4096, '2022-11-06 03:37:19', '2022-11-06 06:34:49', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9160, 'chest_pain_risk_factors', 'Diabetes', 'Diabetes mellitus', 'Presence of &ge;1 of the following:<br />Fasting plasma glucose &ge;126 mg/dL (7.0 mmol/L); or<br />2-h plasma glucose &ge;200 mg/dL (11.1 mmol/L) during an oral glucose tolerance test;<br />Currently taking a medication required to control blood glucose or<br />In a patient with classic symptoms of hyperglycemia or hyperglycemic crisis, a random plasma glucose &ge;200 mg/dL (11.1 mmol/L)', NULL, 4096, '2022-11-06 03:33:25', '2022-11-06 06:37:21', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9168, 'chest_pain_risk_factors', 'Diet', 'Diet and nutrition', 'Diet high in red and processed meats, refined carbohydrates, sweetened beverages, saturated fats, and trans fats. <br />Low intake of vegetables, fruits, legumes, nuts, whole grains, and fish.', NULL, 4096, '2022-11-06 03:38:04', '2022-11-06 06:39:02', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9161, 'chest_pain_risk_factors', 'Dyslipidemia', 'Dyslipidemia', 'History of dyslipidemia that was diagnosed or treated by a clinician. Criteria include documentation of the following:<br /><br />Total cholesterol &gt;200 mg/dL (5.18 mmol/L); or<br /><br />LDL &ge;130 mg/dL (3.37 mmol/L); or<br /><br />Lipoprotein (a) &gt;50 mg/dL (125 nmol/L), or persistent elevations of triglycerides &ge;175 mg/dL (&ge;1.97 mmol/L);<br /><br />Currently receiving antilipidemic treatment', NULL, 4096, '2022-11-06 03:33:47', '2022-11-06 06:44:03', ' Circulation. 2019;139:e1082âe1143.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9169, 'chest_pain_risk_factors', 'family_premature_CAD', 'Family history of premature CAD', 'History of any direct blood relatives (parents, siblings, children) who have had any of the following conditions at age &lt;55 y for male relatives or age &lt;65 y for female relatives:<br /><br />AMI<br />Sudden cardiac death without obvious cause<br />CABG surgery<br />PCI<br />', NULL, 4096, '2022-11-06 03:38:54', '2022-11-06 06:47:52', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9163, 'chest_pain_risk_factors', 'current_smoking', 'Current smoking or vaping', 'A person who has smoked at least 100 cigarettes or e-cigarettes and reports currently smoking tobacco or vaping every day (i.e., daily smoker) or on some days (nondaily smoker)', NULL, 4096, '2022-11-06 03:34:48', '2022-11-06 06:54:39', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9165, 'chest_pain_risk_factors', 'obesity', 'Overweight/ obesity', 'BMI 25&ndash;29.9 kg/m2 (overweight) or &ge;30 kg/m2 (obese)', NULL, 4096, '2022-11-06 03:36:23', '2022-11-06 07:00:51', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9164, 'chest_pain_risk_factors', 'inactivity', 'Physical inactivity', 'Does not meet minimum physical recommendations, defined as &ge;150 min/wk of accumulated<br /><br />moderate-intensity or &ge;75 min/wk of vigorous-intensity aerobic physical activity (or an equivalent combination of moderate and vigorous activity)', NULL, 4096, '2022-11-06 03:35:52', '2022-11-06 06:59:42', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9166, 'chest_pain_risk_factors', 'Stress', 'Stress', 'A life situation that creates an unusual or intense level of stress that may contribute to the development or aggravation of mental disorder, illness, or maladaptive behavior.', NULL, 4096, '2022-11-06 03:36:53', '2022-11-06 07:02:01', NULL);
