INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9205, 'nonatherosclerotic_cad', 'microvascular', '&nbsp;microvascular dysfunction', 'Epicardial or microvascular endothelial or nonendothelial dysfunction that limits myocardial perfusion, most often detected as reduced coronary flow reserve', NULL, 4112, '2022-11-10 13:07:30', '2022-11-10 13:07:30', 'J Am Coll Cardiol. 2021;78:1352-1371.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9206, 'nonatherosclerotic_cad', 'vasospasm', 'vasospasm', 'Intense vasoconstriction (i.e., &gt;90%) of an epicardial coronary artery resulting in compromised myocardial blood flow. <br />Coronary vasospasm can occur either in response to drugs or toxins (e.g., cocaine, 5-fluorouracil) that result in hyper-reactivity of vascular smooth muscles or spontaneously because of disorders in coronary vasomotor tone.', NULL, 4112, '2022-11-10 13:09:58', '2022-11-10 13:09:58', 'Circulation. 2019;139:e891-908.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9207, 'nonatherosclerotic_cad', 'Spontaneous_dissection', 'Spontaneous coronary artery dissection', 'Epicardial coronary artery dissection that is not associated with atherosclerosis or trauma and is not iatrogenic.<br />Predominant mechanism of myocardial injury occurring as a result of spontaneous coronary artery dissection is coronary artery obstruction caused by formation of an intramural hematoma <br />or <br />intimal disruption rather than atherosclerotic plaque rupture or intraluminal thrombus.', NULL, 4112, '2022-11-10 13:12:30', '2022-11-10 13:12:30', 'Circulation. 2018;137:e523-557');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9208, 'nonatherosclerotic_cad', 'INOCA', 'INOCA', 'The following criteria should be met:<br />1. Stable, chronic (&gt;2 mo) symptoms suggesting CAD such as chest discomfort with both classic (e.g., angina pectoris) and atypical features in terms of location, quality, and inciting factors<br />2. Objective evidence for myocardial ischemia from the ECG or a cardiac imaging study (ECG, nuclear imaging, MRI, or spectroscopy) at rest or during stress (exercise, mental, or pharmacological)<br />3. Absence of flow-limiting obstruction by coronary angiography (invasive or CCTA) as defined by any epicardial coronary artery diameter reduction &ge;50% or fractional flow reserve &lt;0.8', 'Ischemia and no obstructive coronary artery disease (INOCA)<br />Mechanisms contributing to INOCA appear multifactorial and may operate alone or in combination.<br />&nbsp;Although these may include hypertension, severe aortic stenosis, severe anemia, type II MI, shunts, certain drugs, HF or cardiogenic shock, Prinzmetal variant angina (coronary spasm), myocardial diseases (eg, myocarditis), congenital heart disease, coronary anomalies, myocardial bridging, and other causes in an occasional patient, underlying mechanisms and appropriate diagnostic and management strategies in these settings are usually apparent. <br />The pathophysiological mechanism for INOCA remains unclear after initial evaluation.', 4112, '2022-11-10 13:17:39', '2022-11-10 13:23:08', 'Circulation. 2017;135:1075-1092.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9209, 'nonatherosclerotic_cad', 'embolism', 'Coronary embolism', 'The blockage of a coronary vessel lumen by air or solid material such as blood clot or other tissues (e.g., adipose tissue, cancer cells) that have migrated from another anatomic site', NULL, 4112, '2022-11-10 13:29:38', '2022-11-10 13:29:38', 'NCI Thesaurus Code: C26759');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9211, 'chest_pain_noncoronary_causes', 'Valvular ', 'Valvular', NULL, NULL, 4113, '2022-11-10 13:42:47', '2022-11-10 13:42:47', 'Circulation. 2021;143:e72-e227');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9212, 'chest_pain_noncoronary_causes', 'Pericarditis', 'Pericarditis', 'Pericarditis classically presents with chest pain that is sharp, pleuritic, and which may be improved by sitting up or leaning forward, although in many instances such findings are not present.<br />A pericardial friction rub may be audible. Widespread ST elevation with PR depression is the ECG hallmark, although changes are nonspecific and may be transient.', 'MRI is also useful as a diagnostic tool.', 4113, '2022-11-10 13:44:38', '2022-11-10 13:44:38', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9210, 'nonatherosclerotic_cad', 'MINOCA', 'MINOCA', 'Contemporary diagnosis and management of patients with myocardial infarction in the absence of obstructive coronary artery disease<br /><br />The diagnosis of MINOCA is made in patients with AMI that fulfills all of the following criteria:<br /><br />1. AMI (modified from the &ldquo;Fourth universal definition of myocardial infarction&rdquo; criteria): Detection of a rise or fall of cTn with &ge;1 value above the 99th percentile URL and corroborative clinical evidence of infarction evidenced by &ge;1 of the following:<br /><br />Symptoms of myocardial ischemia<br />New ischemic ECG changes<br /><br />Development of pathological Q waves<br /><br />Imaging evidence of new loss of viable myocardium or new regional wall motion abnormality in a pattern consistent with an ischemic cause<br /><br />Identification of a coronary thrombus by angiography or autopsy<br /><br />2. Nonobstructive coronary arteries on angiography, defined as the absence of obstructive disease on angiography (i.e., no coronary artery stenosis &ge;50%) in any major epicardial vessel. (Note that additional review of the angiogram may be required to ensure the absence of obstructive disease.) This includes patients with normal coronary arteries (no angiographic stenosis), mild luminal irregularities (angiographic stenosis &lt;30% stenoses), moderate coronary atherosclerotic lesions (stenoses &gt;30% but &lt;50%).<br /><br />3. No specific alternate diagnosis for the clinical presentation. Alternate diagnoses include but are not limited to nonischemic causes such as sepsis, pulmonary embolism, and myocarditis.', NULL, 4112, '2022-11-10 13:38:34', '2022-11-10 13:38:34', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9216, 'chest_pain_noncoronary_causes', 'HCM', 'Hypertrophic cardiomyopathy', 'Disorder of the heart characterized by increased and abnormal hypertrophy of the left ventricle that cannot be explained by loading changes of the heart. It can be with or without LV outflow obstruction.', NULL, 4113, '2022-11-10 13:55:46', '2022-11-10 13:55:46', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9235, 'chest_pain_nocardiac_causes', 'Respiratory', 'Respiratory disorders', 'Pneumothorax/hemothorax, pneumomediastinum, pneumonia, bronchitis, pleural irritation, malignancy', NULL, 4115, '2022-11-10 14:56:07', '2022-11-10 14:56:07', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9213, 'chest_pain_noncoronary_causes', 'Myocarditis', 'Myocarditis', 'Myocarditis is an inflammatory disease of the myocardium resulting from viral infections or postviral immune-mediated responses.<br /><br />Clinical manifestations of myocarditis are varied and include chest pain that is often sharp and reflective of epicardial inflammation involving the pericardium.<br /><br />Myocardial dysfunction often causes fatigue and exercise intolerance, and predominance of heart failure distinguishes myocarditis from pericarditis. Troponin is usually elevated.', NULL, 4113, '2022-11-10 13:47:15', '2022-11-10 13:47:15', 'Circulation. 2021;144:e368âe454');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9233, 'chest_pain_nocardiac_causes', 'Gastrointestinal ', 'Gastrointestinal or hepatobiliary disorders', 'Esophagitis, esophageal rupture, esophageal motility disorder, esophageal spasm, esophageal hypersensitivity, hiatal hernia, gastritis, gastroesophageal reflux, dyspepsia, peptic ulcer disease, intra-abdominal masses (benign and malignant), hepatitis, cholecystitis, cholelithiasis, pancreatitis', 'Among outpatients who present with chest pain, approximately 10%&ndash;20% have a gastrointestinal cause. Gastrointestinal-related chest pain can mimic chest pain related to myocardial ischemia.', 4115, '2022-11-10 14:53:37', '2022-11-10 14:53:37', 'Circulation. 2021;144:e368âe454.');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9236, 'chest_pain_nocardiac_causes', 'pa_embolism', 'Pulmonary embolism', 'Intravascular migration of a venous thrombus to the pulmonary arterial circulation. <br />It is diagnosed by a positive pulmonary angiogram, an unequivocally positive helical CT scan, a high-probability ventilation-perfusion scan, or autopsy.', NULL, 4115, '2022-11-10 14:58:30', '2022-11-10 14:58:30', 'Circ Cardiovasc Qual Outcomes. 2020;13:e000059');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9214, 'chest_pain_noncoronary_causes', 'Heart_failure', 'Heart failure', 'Heart failure is a complex clinical syndrome that results from any structural or functional impairment of ventricular filling or ejection of blood. <br />The cardinal manifestations of heart failure are dyspnea and fatigue, which may limit exercise tolerance, and fluid retention, which may lead to pulmonary or splanchnic congestion or peripheral edema. <br />There is no single diagnostic test for heart failure because it is largely a clinical diagnosis based on a careful history and physical examination.', NULL, 4113, '2022-11-10 13:50:41', '2022-11-10 13:50:41', 'Circulation. 2022;145:e895âe1032');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9223, 'chest_pain_vascular_causes', 'Ao_disease', 'Aortic disease', 'Disease in the thoracic, thoracoabdominal, or abdominal aorta (typically aneurysm)', NULL, 4114, '2022-11-10 14:37:26', '2022-11-10 14:40:48', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9215, 'chest_pain_noncoronary_causes', 'stress_cardiomyopathy', 'Stress cardiomyopathy', 'Stress-induced cardiomyopathy is characterized by acute, usually reversible LV dysfunction in the absence of significant CAD, usually triggered by acute emotional or physical stress.', NULL, 4113, '2022-11-10 13:53:19', '2022-11-10 13:53:19', ' Circ Cardiovasc Qual Outcomes. 2021;14:e000102');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9238, 'chest_pain_nocardiac_causes', 'Drugs', 'Drug use', 'Chest pain related to use of drugs such as cocaine or methamphetamine', NULL, 4115, '2022-11-10 15:06:01', '2022-11-10 15:06:01', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9230, 'chest_pain_vascular_causes', 'hematoma', 'Intramural hematoma', 'An entity in which a hematoma develops in the media of the aortic wall in the absence of a false lumen and intimal tear.<br />Intramural hematoma is diagnosed in the presence of a circular or crescent-shaped thickening of 0.5 mm of the aortic wall in the absence of detectable blood flow.', NULL, 4114, '2022-11-10 14:42:48', '2022-11-10 14:42:48', 'Eur Heart J. 2014;35:2873-2926');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9231, 'chest_pain_vascular_causes', 'Ao_ulcer', 'Penetrating aortic ulcer', 'Ulceration of an aortic atherosclerotic plaque penetrating through the internal elastic lamina into the media. <br />More often in elderly patients, and rarely manifests as signs of organ malperfusion.', NULL, 4114, '2022-11-10 14:45:01', '2022-11-10 14:45:01', 'Eur Heart J. 2014;35:2873-2926');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9237, 'chest_pain_nocardiac_causes', 'Sickle_cell', 'Sickle cell disease', 'Acute chest syndrome is a leading cause of death for patients with sickle cell disease. <br />Although chest pain occurs in most, other manifestations of acute chest syndrome in sickle cell disease/crisis include shortness of breath, fever, arm and leg pain, and the presence of a new density on chest radiography.', '<p data-block-key="sa041">Sickle cell disease is the name for a group of inherited health conditions that affect the red blood cells. The most serious type is called sickle cell anaemia.</p><p data-block-key="g6d4f">People with sickle cell disease produce unusually shaped red blood cells that can cause problems because they do not live as long as healthy blood cells and&nbsp;can block blood vessels.</p><p data-block-key="mum4i">Sickle cell disease is a serious and lifelong health condition, although treatment can help manage many of the symptoms.</p><p data-block-key="sa041">&nbsp;</p>', 4115, '2022-11-10 15:04:39', '2022-11-10 15:04:39', ' Circulation. 2021;144:e368âe454');
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (2860, 'tr_structure', '4', 'Ebstein', 'A congenital malformation of the tricuspid valve and right ventricle that is characterized by downward (apical) displacement of the functional annulus, usually involving the septal and inferior (posterior) leaflets.', NULL, 2883, '2016-10-19 03:47:05', '2022-11-11 00:44:07', NULL);
